
CREATE TABLE bs_binh_luan (id int NOT NULL,
  id_sach int DEFAULT NULL,
  id_nguoi_dung int DEFAULT NULL,
  noi_dung text,
  id_binh_luan_cha int DEFAULT NULL,
  ngay_binh_luan int DEFAULT NULL,
  trang_thai int DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_chat_truc_tuyen (
  id int NOT NULL,
  id_nguoi_dung int DEFAULT NULL,
  ip varchar(255) DEFAULT NULL,
  dang_online int DEFAULT NULL,
  da_duoc_tra_loi int DEFAULT NULL,
  session_id_chat varchar(255) DEFAULT NULL,
  ho_ten varchar(255) DEFAULT NULL,
  email varchar(255) DEFAULT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE bs_chi_tiet_chat (
  id int NOT NULL,
  session_id_chat varchar(255) DEFAULT NULL,
  id_nguoi_dung_tl int DEFAULT NULL,
  time_chat int DEFAULT NULL,
  noi_dung varchar(255) DEFAULT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE bs_chi_tiet_don_hang (
  id int NOT NULL,
  id_don_hang int DEFAULT NULL,
  id_sach varchar(255) DEFAULT NULL,
  so_luong varchar(255) DEFAULT NULL,
  don_gia varchar(255) DEFAULT NULL,
  thanh_tien varchar(255) DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_danh_gia_sach (
  id int NOT NULL,
  danh_gia int DEFAULT NULL,
  id_nguoi_dung int DEFAULT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE bs_don_hang (
  id int NOT NULL,
  ma_don_hang varchar(255) DEFAULT NULL,
  tong_tien int DEFAULT NULL,
  ngay_dat timestamp DEFAULT NULL,
  id_nguoi_dung varchar(255) DEFAULT NULL,
  ho_ten_nguoi_nhan varchar(255) DEFAULT NULL,
  email_nguoi_nhan varchar(255) DEFAULT NULL,
  so_dien_thoai_nguoi_nhan varchar(255) DEFAULT NULL,
  trang_thai int DEFAULT NULL,
  dia_chi_nguoi_nhan varchar(255) DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_loai_nguoi_dung (
  id int NOT NULL,
  ten_loai_nguoi_dung varchar(255) DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_loai_sach (id int NOT NULL,
  ten_loai_sach varchar(255) DEFAULT NULL,
  id_loai_cha int DEFAULT NULL,
  sap_xep varchar(255) DEFAULT NULL,
  trang_thai int DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_loai_tin_tuc (
  id int NOT NULL,
  ten_loai_tin varchar(255)  DEFAULT NULL,
  alias varchar(255)  DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_menu (
  id int NOT NULL,
  tieu_de varchar(255) DEFAULT NULL,
  alias varchar(255) DEFAULT NULL,
  trang_thai int DEFAULT NULL,
  menu_cha int DEFAULT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE bs_nguoi_dung (
  id int NOT NULL,
  tai_khoan varchar(255) DEFAULT NULL,
  mat_khau varchar(255) DEFAULT NULL,
  id_loai_user varchar(255) DEFAULT NULL,
  id_phan_quyen varchar(255) DEFAULT NULL,
  ho_ten varchar(255) DEFAULT NULL,
  email varchar(255) DEFAULT NULL,
  ngay_sinh varchar(255) DEFAULT NULL,
  dia_chi varchar(255) DEFAULT NULL,
  diem_tich_luy int DEFAULT NULL,
  ngay_dang_ky timestamp DEFAULT NULL,
  thoi_gian_dang_nhap timestamp DEFAULT NULL,
  active_code varchar(255) DEFAULT NULL,
  avatar varchar(255) DEFAULT NULL,
  so_lan_reset int DEFAULT NULL,
  dien_thoai varchar(255) DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_nha_xuat_ban (
  id int NOT NULL,
  ten_nha_xuat_ban varchar(255) DEFAULT NULL,
  dia_chi varchar(255) DEFAULT NULL,
  dien_thoai varchar(255) DEFAULT NULL,
  email varchar(255) DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_nxb_tre (
id int NOT NULL,
ten_sach varchar(255) DEFAULT NULL,
  id_tac_gia int DEFAULT NULL,
  gioi_thieu text,
