  id_nha_xuat_ban int DEFAULT NULL,
  so_trang int DEFAULT NULL,
  ngay_xuat_ban varchar(50) DEFAULT NULL,
  kich_thuoc varchar(255) DEFAULT NULL,
  sku varchar(255) DEFAULT NULL,
  trong_luong varchar(255) DEFAULT NULL,
