  hinh varchar(255) DEFAULT NULL,
  don_gia int DEFAULT NULL,
  gia_bia int DEFAULT NULL,
  noi_bat int DEFAULT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE bs_sach (id int NOT NULL,
ten_sach varchar(255) DEFAULT NULL,
