CREATE TABLE bs_sach_yeu_thich (id int NOT NULL,
 id_nguoi_dung int DEFAULT NULL,
  id_sach varchar(255)  DEFAULT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE bs_slide_banner (id int NOT NULL,
ten_slide varchar(255) DEFAULT NULL,
  hinh varchar(255) DEFAULT NULL,
  trang_thai varchar(255) DEFAULT NULL,
  PRIMARY KEY (id)
);


CREATE TABLE bs_tac_gia (
id int NOT NULL,
ten_tac_gia varchar(255) DEFAULT NULL,
  ngay_sinh date DEFAULT NULL,
