);


CREATE TABLE bs_tin_tuc (
id int NOT NULL,
tieu_de_tin varchar(255)  DEFAULT NULL,
  noi_dung_tom_tat text ,
  noi_dung_chi_tiet text ,
  trang_thai int DEFAULT NULL,
  hinh_dai_dien varchar(255)  DEFAULT NULL,
