INSERT INTO bs_tac_gia VALUES ('26', 'Dale Carnegie', null, '<div class=""image""><img src=""http://tikicdn.com/media/author/2/28efce79a06bc149_large.jpeg"" height=""196"" width=""140"" alt=""""></div>Dale Breckenridge Carnegie (trước kia là Carnagey cho tới năm 1922 và có thể một thời gian muộn hơn) (24 tháng 11 năm 1888 – 1 tháng 11 năm 1955) là một nhà văn và nhà thuyết trình Mỹ và là người phát triển các lớp tự giáo dục, nghệ thuật bán hàng, huấn luyện đoàn thể, nói trước công chúng và các kỹ năng giao tiếp giữa mọi người. Ra đời trong cảnh nghèo đói tại một trang trại ở Missouri, ông là tác giả cuốn Đắc Nhân Tâm, được xuất bản lần đầu năm 1936, một cuốn sách thuộc hàng bán chạy nhất và được biết đến nhiều nhất cho đến tận ngày nay. Ông cũng viết một cuốn tiểu sử Abraham Lincoln, với tựa đề Lincoln con người chưa biết, và nhiều cuốn sách khác. Carnegie là một trong những người đầu tiên đề xuất cái ngày nay được gọi là đảm đương trách nhiệm, dù nó chỉ được đề cập tỉ mỉ trong tác phẩm viết của ông. Một trong những ý tưởng chủ chốt trong những cuốn sách của ông là có thể thay đổi thái độ của người khác khi thay đổi sự đối xử của ta với họ.');
INSERT INTO bs_tac_gia VALUES ('27', 'Fujiko.F.Fujio', null, null);
INSERT INTO bs_tac_gia VALUES ('28', 'Tô Hồng Vân', null, 'Tất cả các côngviệc từng làm, hoạt động từng tham gia đều có liên quan đến viết lách và trẻ con:\r\n\r\n3 năm liền đoạt giải trong cuộc thi viết thư Quốc tế UPU, cao nhất là Giải I Quốc gia và Huy chương\r\n\r\nĐồng Quốc tế năm 1998');
